# Read a JSON file into a Godot dictionary
func read_json_file(filename: String):
	var ff = FileAccess.open(filename, FileAccess.READ)
	if not ff:
		return {}
	var json_string: String = ff.get_as_text()
	var json = JSON.new()
	var error = json.parse(json_string)
	if error != OK:
		return {}
	return json.data

